INSERT INTO public.schools (name, description, course_requirements_expression, unit_requirements, other_requirements, src) VALUES
('Leavey School of Business', 'The Leavey School of Business confers the Bachelor of Science in Commerce with majors in accounting, accounting and information systems, economics, finance, individual studies, management, marketing, and management information systems. The school also offers departmental minors in business analytics, management information systems, marketing, and real estate, and interdisciplinary minors in entrepreneurship, international business, retail studies, and sustainable food systems.', '(BUSN70 & OMIS15) & BUSN85 & (ECON1 & ECON2 & ECON3) & MGMT71 & (ACTG11 & ACTG12) & 2(OMIS40 | OMIS41 | ECON41 | ECON42) & OMIS34 & (FNCE121 & MGMT160 & MKTG181 & OMIS108) & MGMT162', '[{"requirementDescription":"Total quarter units for Bachelor of Science in Commerce","measuredBy":"units","numRequired":175,"typeRequired":"Any","numRequiredFromSCU":0,"mustBeFromDepartmentCode":""},{"requirementDescription":"Upper-division quarter units for Bachelor of Science in Commerce","measuredBy":"units","numRequired":60,"typeRequired":"Upper Division","numRequiredFromSCU":0,"mustBeFromDepartmentCode":""}]', '[{"requirementName":"Prerequisite Requirements","requirementDescription":"Prerequisite requirements must be successfully completed or in progress prior to enrollment in the course that requires the prerequisite."},{"requirementName":"Degree Requirements","requirementDescription":"Students must satisfy the university Core Curriculum, the Leavey School of Business curriculum, and their departmental major."},{"requirementName":"Minors and Interdisciplinary Minors","requirementDescription":"Minors offered include business analytics, management information systems, marketing, real estate, entrepreneurship, international business, retail studies, and sustainable food systems; specific requirements are detailed in the respective department and interdisciplinary minors sections."},{"requirementName":"Individual Studies Petition Requirements","requirementDescription":"To declare the Individual Studies major, students must submit and obtain approval of a petition demonstrating at least one year of full-time enrollment at SCU, a minimum 3.0 overall GPA, a clear description of the proposed program and learning objectives, evidence that no existing program meets the objectives, a plan of study with at least eight upper-division courses beyond core requirements, and signed approval of a tenured or tenure-track faculty advisor; petition is reviewed and approved by the faculty director of Undergraduate Business Programs."}]', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/undergraduate-degrees.html#fe712c3a9716'),
('College of Arts and Sciences', 'The College of Arts and Sciences confers Bachelor of Arts degrees in art history, chemistry, classics, communication, English, French and Francophone studies, history, individual studies, Italian studies, music, philosophy, religious studies, Spanish studies, studio art, theatre arts, and gender and sexuality studies; and Bachelor of Science degrees in social sciences and natural sciences disciplines. It also offers a wide array of minors and interdisciplinary minors across diverse fields.', '', '[{"requirementDescription":"Minimum quarter units of credit for the degree","measuredBy":"units","numRequired":175,"typeRequired":"Any","numRequiredFromSCU":0,"mustBeFromDepartmentCode":""},{"requirementDescription":"Minimum upper-division quarter units of credit for the degree","measuredBy":"units","numRequired":60,"typeRequired":"Upper Division","numRequiredFromSCU":0,"mustBeFromDepartmentCode":""}]', '[{"requirementName":"Core Curriculum and Major","requirementDescription":"Satisfy the requirements of the undergraduate Core Curriculum and the major"}]', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/undergraduate-degrees.html#02a28019a4b8'),
('School of Engineering', 'The School of Engineering confers the Bachelor of Science degree with majors in Bioengineering, Civil Engineering, Computer Science and Engineering, Web Design and Engineering, Electrical Engineering, Electrical and Computer Engineering, General Engineering, and Mechanical Engineering. Specialized programs in Civil Engineering, Computer Science and Engineering, Electrical and Computer Engineering, Electrical Engineering, and Mechanical Engineering are ABET-accredited; Bioengineering, General Engineering, and Web Design and Engineering are not yet accredited. The school also offers minors in Aerospace Engineering; Bioengineering; Computer Science and Engineering; Construction Management; Electrical Engineering; Electrical and Computer Engineering; General Engineering; Healthcare Innovation and Design; Mechanical Engineering; and Technical Innovation, Design Thinking, and the Entrepreneurial Mindset. All undergraduate engineering programs require extensive mathematics and natural science sequences, strict enforcement of prerequisites, and completion of the Undergraduate Core Curriculum.', '', '[]', '[{"requirementName":"Bachelor of Science Requirements","requirementDescription":"Complete the minimum number of units specified for the major and satisfy the Undergraduate Core Curriculum."},{"requirementName":"Critical Thinking & Writing","requirementDescription":"One two-course sequence in composition: CTW 1 and 2."},{"requirementName":"Advanced Writing","requirementDescription":"ENGL 181."},{"requirementName":"Religion, Theology & Culture","requirementDescription":"Three courses approved to satisfy the core requirements."},{"requirementName":"Cultures & Ideas 1 and 2","requirementDescription":"One course sequence from the approved list of Cultures & Ideas sequences."},{"requirementName":"Cultures & Ideas 3","requirementDescription":"One course from the approved list."},{"requirementName":"Mathematics and Natural Science","requirementDescription":"Course requirements are specified in the respective major requirements."},{"requirementName":"Second Language","requirementDescription":"Recommended proficiency in one foreign language; satisfied by two years of high school study."},{"requirementName":"Social Science","requirementDescription":"One course from the approved list."},{"requirementName":"Civic Engagement","requirementDescription":"One course from the approved list or a combination of ENGR 1 and a senior design project."},{"requirementName":"Ethics","requirementDescription":"One course in general or applied ethics from the approved list."},{"requirementName":"Diversity","requirementDescription":"One course from the approved list."},{"requirementName":"Arts","requirementDescription":"One course from the approved list or a combination of ENGL 181 and a senior design project."},{"requirementName":"Science, Technology & Society","requirementDescription":"One course from the approved list (which includes ENGR 1) or a combination of ENGL 181 and a senior design project."},{"requirementName":"Experiential Learning for Social Justice","requirementDescription":"One course (or activity) approved to satisfy experiential learning."},{"requirementName":"Pathways","requirementDescription":"Three courses with a common theme approved for a declared Pathway."}]', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/undergraduate-degrees.html#54b1f7d6146d');
INSERT INTO public.departments_and_programs (name, description, majors_offered, minors_offered, emphases, school_name, src) VALUES
('Biology', 'The Department of Biology offers courses exploring a vast array of biological questions, including core courses for non-majors, introductory curriculum for life science majors, and advanced courses leading to a Bachelor of Science degree, with emphasis on laboratory and field work and student involvement in faculty research.', '["Biology"]', '["Biology"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/biology.html#3aca1358926e'),
('Anthropology', 'The Department of Anthropology offers a Bachelor of Science in Anthropology, providing analytical and methodological training in human biological, archaeological, and cultural diversity. The department also offers a minor, several optional emphases, and an honors thesis option.', '["Anthropology"]', '["Anthropology"]', '["Applied Anthropology","Archaeology","Biological Anthropology"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/anthropology.html#f7bf97d9b9e0'),
('Art and Art History', 'The Department of Art and Art History offers Bachelor of Arts degree programs in two majors—Art History and Studio Art—providing students with a thorough understanding of the history and practice of art. Faculty encourage interdisciplinary connections through courses that fulfill Core Curriculum and Honors Program requirements.', '["Art History","Studio Art"]', '["Animation and Illustration","Art History","Arts Management","Graphic Design","Studio Art"]', '["Arts Management","Graphic Design"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/art-and-art-history.html#f7994386b4fd'),
('Medical and Health Humanities', 'Students in the Medical and Health Humanities minor apply the breadth of the Humanities to key questions and problems that define medicine and health. By exploring the history of medicine, narratives about sickness and health, religious and philosophical beliefs about disease and wellness, and the ethics of health care, students develop a deeper understanding of both the practices of medicine and health sciences as well as the experiences of illness and disability across cultures. Courses emphasize humanistic skills that graduate health and medical programs prize. This minor is open to students majoring in any discipline.', '[""]', '["Medical and Health Humanities"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/medical-and-health-humanities.html#55b3941878ea'),
('Musical Theatre', 'Offers experience and training in music, theatre, and dance, as well as musical theatre history. Objectives include entry-level proficiency for a performance career, enhancement in teaching or graduate study; audition techniques; performance in acting, singing, and theatrical dance; and knowledge of cultural history and various forms of musical theatre. Repertoire choices may be made with instructors to focus vocal work in a Broadway or opera style.', '[""]', '["Musical Theatre"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/musical-theatre.html#339634e37c1a'),
('International Business', 'The minor in international business is designed to educate students in a broad range of management and business skills in a global context. The program includes coursework in language, social science, and international business, and is open to all undergraduate students. The minor provides students with an understanding of the social, economic, and political context of international business, the language communication skills, and the business skills to be effective managers in a global marketplace. Students are encouraged to participate in a study abroad program or internship to complement this minor.', '[""]', '["International Business"]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/international-business.html#0df3c5e2a1e5'),
('Child Studies', 'The Child Studies program prepares students for careers working with children and families in school or community settings. The curriculum emphasizes social justice, anti-racist practice, and cultural competence. Students work extensively with children and families in local schools and community-based programs, and the faculty addresses issues affecting marginalized populations—including education policy, the preschool-to-prison pipeline, socio-emotional processes, political dynamics, community organizing, and institutional change. Graduates earn a Bachelor of Science and are prepared for graduate studies and professional vocations in fields such as teacher education, social work, counseling, psychology, law, speech and language pathology, occupational therapy, and nonprofit agencies.', '["Child Studies"]', '[""]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/child-studies.html#07937c069e5d'),
('Individual Studies', 'The Individual Studies Program (ISP) allows students to design a custom, multidisciplinary course of study leading to a B.A. or a B.S. in social science. Coursework must be drawn from a significant number of existing offerings across multiple disciplines (independent study and directed readings are discouraged), with no more than four study‐abroad courses counting toward the major. Each ISP student must secure a continuing SCU faculty member as an advisor to review and approve the program of study, provide at least annual advising, and complete the student’s pre‐graduation audit.', '["Individual Studies"]', '[""]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/individual-studies.html#37d707d78140'),
('Premodern Studies', 'The minor in Premodern Studies offers a cross-disciplinary, interdisciplinary program of study of the global premodern period between late antiquity and 1800, examining how premodern people, events, and institutions shaped the modern world. Completion of the minor is noted on the student’s transcript and accompanied by a certificate of accomplishment.', '[""]', '["Premodern Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/premodern-studies.html#067d0e2bcc5b'),
('Sustainability', 'The sustainability minor helps students discover the connections and balance among a healthy environment, just societies, and a vibrant economy that meet all people’s fundamental needs currently and in the future, especially those of the global poor. Courses explore the environmental, societal, and economic aspects of sustainability, and how to take action to improve sustainability.', '[""]', '["Sustainability"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/sustainability.html#6ecd3ff74ca4'),
('Physics', 'The Department of Physics offers BS degrees in Physics (with traditional and biophysics tracks) and in Engineering Physics, as well as a minor in Physics. It provides lecture and laboratory instruction, research opportunities in faculty projects and internships, and prepares students for careers in industry, government labs, academia, patent law, medicine, and graduate study.', '["Physics","Engineering Physics"]', '["Physics"]', '["Physics Track","Biophysics Track"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/physics.html#6598cf74a8fa'),
('Gerontology', 'Gerontology is an interdisciplinary field focused on the study of older adults, typically defined as over age 65. Students in the minor gain hands-on experience working with and for older adults and examine how aging intersects with health, mental health, disease, longevity, and diverse life experiences.', '[""]', '["Gerontology"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/gerontology.html#0c47a1ba82cc'),
('Mechanical Engineering', 'Mechanical Engineering applies the fields of physics, mathematics, and materials science to the design and realization of mechanical and thermal systems. For over a century mechanical engineers have played a central role in creating the infrastructure of modern society, while addressing emerging interdisciplinary challenges. The undergraduate curriculum provides a rigorous theoretical foundation coupled with hands-on laboratories and projects, rooted in the Jesuit tradition and emphasizing ethical and social impacts of engineering.', '["Mechanical Engineering"]', '["Mechanical Engineering","Aerospace Engineering"]', '[""]', 'School of Engineering', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/mechanical-engineering.html#a358a81067c1'),
('Chemistry and Biochemistry', 'The Department of Chemistry and Biochemistry offers B.S. degrees in Chemistry and Biochemistry and a B.A. in Chemistry, all ACS-accredited, preparing students for graduate study and professional careers in chemistry, biochemistry, medicine, law, business, and teaching. The curriculum emphasizes faculty-mentored undergraduate research (CHEM 182–184), flexible elective sequences, supporting math and physics coursework, and opportunities for study abroad. A minor in Chemistry is also available.', '["Chemistry","Chemistry","ACS Certified","Biochemistry","Biochemistry","ACS Certified","Chemistry"]', '["Chemistry"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/chemistry-and-biochemistry.html#cb812009d91c'),
('Economics', 'As one of the social sciences, economics studies how the choices we make as individuals—as consumers and producers, as savers and investors, as managers and employees, as citizens and voters—combine to determine how society uses its scarce resources to produce and distribute goods and services. This practical discipline provides insights into important issues such as the determinants of wealth and poverty—unemployment, inflation, international trade, and economic growth; as well as success and failure in the marketplace. The rigorous, systematic analysis that the study of economics brings to bear on these and other real-world issues provides excellent preparation for careers in both the private and the public sectors, as well as for graduate study in economics, business, public policy, and law. Economics graduates pursue varied careers in business, law, banking and finance, government service, education, and private consulting. Students considering graduate study in economics leading to a master’s or doctoral degree are strongly encouraged to meet with their advisor as early as possible to plan an appropriate course of study.', '["Economics"]', '["Economics"]', '["Data Analysis for Economics","Mathematical Economics"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/economics.html#c89c2dd151c8'),
('Psychology', 'The Department of Psychology offers a degree program leading to the bachelor of science in psychology. Psychology is the study of behavior, emotion, and thought using the scientific method. At the undergraduate level, the study of psychology is part of a liberal arts education. A major in psychology lays the groundwork for various careers and advanced studies, including the pursuit of graduate degrees needed for the professional practice of psychology.', '["Psychology"]', '[""]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/psychology.html#cbd09cb3c353'),
('Catholic Studies', 'The minor in Catholic studies, open to students from all departments, is an interdisciplinary program for the study of the intellectual tradition of the Catholic faith. The minor is designed for intensive study of Catholicism as a faith embedded in many cultures and for the critical retrieval of the Catholic intellectual tradition through dialogue with contemporary thought under the rubrics of a variety of academic disciplines. Catholic studies minors are assigned a faculty mentor who guides them through the program. In conjunction with the Ignatian Center for Jesuit Education, the Catholic Studies program sponsors intellectual, cultural, social, and religious opportunities for both students and faculty.', '[""]', '["Catholic Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/catholic-studies.html#2ae11056eba4'),
('Entrepreneurship', 'The entrepreneurship minor provides students interested in developing a business or working in a startup the chance to explore that career option and acquire skills for success. Students learn the venture creation process—from generating and developing a business concept, applying analytical tools to identify and evaluate opportunities, creating and evaluating a business plan, to analyzing funding sources. A critical element is an internship with an emerging for-profit or social benefit organization. The minor is open to all students via application to the Management and Entrepreneurship Department. F-1 international students should consult with the Global Engagement Office about immigration requirements for academic internships. Business school students may begin after earning 60 units; non-business students should ideally complete prerequisites by their sophomore year.', '[""]', '["Entrepreneurship"]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/entrepreneurship.html#e14ad917a964'),
('Applied Mathematics', 'The Department of Applied Mathematics offers only graduate degree programs and operates in a service mode at the undergraduate level. Undergraduate courses offered by the department have been designed to bridge mathematical theory and engineering applications.', '[""]', '[""]', '[""]', 'School of Engineering', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/applied-mathematics.html#8a4314efc9cf'),
('Retail Studies', 'The Retail Studies minor, offered by the Retail Management Institute (RMI) in SCU’s Leavey School of Business, provides an in-depth hands-on experience preparing students for diverse roles in retail—including e-commerce, buying, merchandising, planning and allocation, digital marketing, and supply chain management. The curriculum consists of core foundational courses in junior and senior year, a two-quarter senior seminar sequence, a junior-year internship, and opportunities for corporate visits, conference participation, and scholarships. Admission is by the executive director based on criteria such as a minimum 3.3 GPA, analytical skills, creative thinking, and prior exposure to retail.', '[""]', '["Retail Studies"]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/retail-studies.html#bb8a363b267a'),
('International Studies', 'The International Studies Minor enables students to study global issues or problems, or focus on a geographic area—Africa, Europe, or Latin America—through an interdisciplinary lens. It provides a framework for adding a global dimension to a major or preparation for a career working with diverse, intercultural teams. The minor consists of four Foundation courses, three Emphasis courses (either Global Thematic or Area Studies), and a Capstone experience.', '[""]', '["International Studies"]', '["Global Thematic Emphasis","Area Studies Emphasis"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/international-studies.html#386d3684e266'),
('Urban Education', 'The interdisciplinary minor in Urban Education provides Santa Clara undergraduate students seeking to become elementary or secondary teachers with foundational coursework in educational theory, urban school observation and reflective experiences, constitutional history of the United States, and the sociological and psychological foundations of education. It also focuses on societal problems such as poverty, crime, and prejudice and how these issues impact today’s youth and families. The minor is recommended for students from diverse majors interested in careers working directly with children and families from multicultural and multifaceted backgrounds.', '[""]', '["Urban Education"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/urban-education.html#d630eb7d4fa1'),
('Latin American Studies', 'The interdisciplinary minor in Latin American Studies provides students with global competence, cross-cultural awareness and communication, appreciation of social diversity and inclusion, and understanding of Latin American region and diaspora communities through language proficiency and targeted coursework. It prepares students for careers in business, public service, marketing, law, nonprofit organizations, and for advanced study in social sciences.', '[""]', '["Latin American Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/latin-american-studies.html#bb80caa57102'),
('Environmental Studies and Sciences', 'The Department of Environmental Studies and Sciences (ESS) offers interdisciplinary BS programs in environmental science and environmental studies, and a minor in environmental studies. These programs combine foundational courses in natural and social sciences with seminars, colloquia, research or internship experiences, capstone projects, study abroad opportunities, and faculty advising to prepare students to address 21st-century environmental challenges.', '["Environmental Science","Environmental Studies"]', '["Environmental Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/environmental-studies-and-sciences.html#85643ab4ddbb'),
('Pre-Law', 'Santa Clara University’s Pre-Law program provides undergraduates with advising and resources to build a strong foundation for law school. Students work with designated pre-law advisors to assess fit for a legal career, understand the LSAT and application process, and prepare for employment as attorneys. While no specific major or curriculum is required, students are encouraged to choose courses that develop analytical ability, research skills, close reading, logical reasoning, verbal proficiency, and precision in written and oral work. The program also offers a non-exhaustive list of elective courses across a variety of departments that can help strengthen relevant skills and perspectives on legal issues.', '[""]', '[""]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/pre-law.html#e5732581652f'),
('Information Systems & Analytics', 'Undergraduate study in the Department of Information Systems and Analytics explores the use of computer information systems and analytical decision-making methods in organizations. The department offers a major and a minor in management information systems (MIS), an interdepartmental major in accounting and information systems (AIS), and a minor in business analytics for business students. MIS Major/Minor and AIS Major programs are STEM-designated.', '["management information systems","accounting and information systems"]', '["management information systems","business analytics"]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/information-systems-analytics.html#25ebf5158560'),
('Neuroscience', 'A Bachelor of Science degree in neuroscience at SCU provides the scientific foundation to understand the nervous system at multiple levels—from molecular mechanisms to patient symptomatology—using interdisciplinary approaches that integrate biological, computational, and psychological perspectives. Students examine interactions between neurological and environmental factors in disease and development, engage with ethical issues in neuroscience research and practice, and prepare for careers in health care, policy, business, law, biomedical and clinical research, or for graduate studies in neuroscience-related fields.', '["Neuroscience"]', '[""]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/neuroscience.html#cb9f443ad798'),
('Biotechnology', 'The minor in biotechnology provides students with a scientific foundation in biotechnology, its ethical and contemporary issues, and hands-on laboratory skills, culminating in a research internship experience. It is ideal for those planning careers in the biotechnology industry or advanced degrees in molecular biology, cell biology, or biochemistry.', '[""]', '["Biotechnology"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/biotechnology.html#9442f7af5e77'),
('Health Professions Advising', 'The Santa Clara University Health Professions Advising program advises, mentors, supports, and educates pre-health students preparing for careers such as dentistry, medicine (allopathic and osteopathic), physical therapy, optometry, pharmacy, and more. Directed by Dr. Brian J. McNelis, the program uses national acceptance data and best practices to guide individualized advising. Advising is delivered through a Canvas Camino “course” (with about 400 students and 10,000 page-views per month) and Drahmann Center appointments, and is reinforced by 16 peer advisors who offer one-on-one appointments, drop-in advising, workshops, and mentorship groups. First-year students participate in a structured mentorship program, and an Executive Board oversees alumni outreach, community building, and program development. The program’s Camino modules cover introduction to the program, advising resources, curriculum planning, career exploration, experiential opportunities, testing support, application support, conferences, student groups, certifications, and post-graduation resources. Additional resources include quick guides for pre-medical, pre-PA, pre-pharmacy, pre-PT, and pre-optometry paths, and alumni engagement via a LinkedIn group and Bronco Exchange collaboration.', '[""]', '[""]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/pre-health-sciences.html#4766d405b29e'),
('Sustainable Food Systems', 'The Sustainable Food Systems minor provides students with the opportunity to explore the intersection of global food systems and sustainability. Courses address the cultural aspects of food, food policies, nutrition and public health, social justice, sustainability, and opportunities for innovation and change. Seven courses are required, including two courses each in the Cultural/Societal, Sustainable Development, and Innovation and Change dimensions, and one Experiential Learning course. Four of the seven must be upper-division, and no more than three courses may come from a student’s major.', '[""]', '["Sustainable Food Systems"]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/sustainable-food-systems.html#b49585ba1534'),
('Public Health', 'The Department of Public Health in the College of Arts and Sciences offers a B.S. in Public Health Science with two emphases (Health Science and Health and Society), manages the Global Health Pathway of the Core, and also offers a minor in Public Health.', '["Public Health Science"]', '["Public Health"]', '["Health Science","Health and Society"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/public-health-department.html#509f0dd05b17'),
('Finance', 'Finance is at the center of well-managed businesses, from high technology companies to mutual fund companies. Development of knowledge and managerial skills in the corporate and investment settings are the major goals of the finance program. Graduates with a degree in finance pursue careers as corporate financial officers, traders, investment managers, financial analysts, financial planners, investment bankers, stockbrokers, regulators, and other specialties. Corporate finance officers manage the assets and value of corporations. They examine which new products and investments will be profitable, analyze the most cost-effective ways to produce them, and determine where to get the money needed to fund new ventures. Personal financial planners and stockbrokers help people make wise investments by selecting good stocks and assembling efficient portfolios. Students in finance also learn how to understand and analyze information from capital markets, engage in mergers and acquisitions, and undertake investments in new ventures, real estate, and international markets.', '["Finance"]', '["Real Estate"]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/finance.html#7428eed349a9'),
('Arabic, Islamic, and Middle Eastern Studies', 'The interdisciplinary minor in Arabic, Islamic, and Middle Eastern Studies (AIMES) provides an introduction to the peoples, cultures, politics, religions, and societies of the Middle East and North Africa, including diaspora communities. Students sample methodologies from anthropology, art history, literary criticism, history, political science, and religious studies, preparing them for engagement with contemporary issues or advanced study.', '[""]', '["Arabic","Islamic","and Middle Eastern Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/arabic-islamic-and-middle-eastern-studies.html#abc06ee976b4'),
('General Engineering', 'The Department of General Engineering offers a Bachelor of Science in Engineering designed to provide students a technical degree with customizable concentrations, as well as minors in General Engineering and in Technical Innovation, Design Thinking, and the Entrepreneurial Mindset.', '["General Engineering"]', '["General Engineering","Technical Innovation","Design Thinking","and the Entrepreneurial Mindset"]', '["BioInnovation and Design"]', 'School of Engineering', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/general-engineering.html#f9a9769c11c2'),
('Accounting', 'The Department of Accounting provides high-quality instruction, conducts research on accounting issues, and serves students, alumni, the profession, and the business community. It offers a major in Accounting and, jointly with the Information Systems & Analytics Department, a STEM-designated Accounting and Information Systems major effective September 2023.', '["Accounting","Accounting and Information Systems"]', '[""]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/accounting.html#88aede57b843'),
('Asian Studies', 'The Asian Studies minor provides an introduction to the cultures and languages of Asia through courses in multiple disciplines. Students sample different dimensions of Asian cultures via approved art history, communication, history, philosophy, political science, religious studies, gender and sexuality studies, and modern language offerings. Courses on the Asian American experience (ethnic studies) do not count toward the minor. Students are strongly encouraged to participate in University-approved study abroad programs in Asia.', '[""]', '["Asian Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/asian-studies.html#89124a1b339a'),
('Mathematics and Computer Science', 'Offers Bachelor of Science degrees in Mathematics and Computer Science with preparation for graduate studies, secondary teaching, or research careers. The Mathematics major includes optional emphases in applied mathematics, data science, financial mathematics, mathematical economics, and mathematics education; the Computer Science major includes emphases in algorithms and complexity, data science, security, software, or student-designed. Minors in mathematics and computer science are available. The department supports talent development through special sections, seminars, individual conferences, directed study, and research.', '["Mathematics","Computer Science"]', '["Mathematics","Computer Science"]', '["Applied Mathematics","Data Science","Financial Mathematics","Mathematical Economics","Mathematics Education","Algorithms and Complexity","Data Science","Security","Software","Individual"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/mathematics-and-computer-science.html#4d8620d8dfad'),
('Pre-Teaching', 'Santa Clara University’s Pre-Teaching program, advised by Melina Johnson, prepares undergraduates for California multiple-subject and single-subject teaching credentials (elementary, middle and high school). Accredited by the California Commission on Teacher Credentialing, it is offered through the School of Education and Counseling Psychology and is combined with a Master of Arts in Teaching (MAT). The program includes methods for instructing English learners and offers bilingual authorization in Spanish. Students are encouraged to complete an interdisciplinary minor in Urban Education through the College of Arts and Sciences.', '[""]', '[""]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/pre-teaching.html#183d3da63088'),
('Gender and Sexuality Studies', 'The Department of Gender and Sexuality Studies provides an integrated, interdisciplinary approach to understanding the social and cultural constructions of gender and sexuality, examining intersections with class, sexuality, ethnicity, disability, age, nationality, and more. The curriculum offers a solid foundation in feminist theory, gender justice, and prepares students for leadership roles, graduate study, and careers involving gender and sexuality concerns.', '["Gender and Sexuality Studies"]', '["Gender and Sexuality Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/gender-and-sexuality-studies.html#81c027c34540'),
('Classics', 'Classics is the study of all aspects of the life and culture of ancient Greece and Rome in their Mediterranean context and their enduring relevance to modern life. The department offers all levels of ancient Greek and Latin as well as multidisciplinary courses exploring Western literature, history, art, mythology, philosophy, religion, ethics, government, science, architecture, engineering, and urban planning. Most courses require no prior language proficiency and are open to all students; Latin or ancient Greek may satisfy the second language requirement. Classics provides an ideal liberal arts curriculum for diverse career paths.', '["Classics"]', '["Classics"]', '["Classical Languages and Literatures","Classical Studies","Ancient Studies","Classical Languages and Literatures","Classical Studies","Ancient Studies"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/classics.html#23ebc4ecf83a'),
('Civil, Environmental and Sustainable Engineering', 'The Department of Civil, Environmental and Sustainable Engineering offers a Bachelor of Science in Civil Engineering that develops graduates capable of planning, designing, building, maintaining, and rehabilitating the complex technological, social, and environmental infrastructure systems on which modern society relies. The curriculum emphasizes sustainability and resilience in design, strong communication skills, and preparation for lifelong learning. Graduates are prepared to enter professional practice or pursue advanced studies in any civil engineering discipline.', '["Civil Engineering"]', '["Construction Management"]', '[""]', 'School of Engineering', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/civil-environmental-and-sustainable-engineering.html#ebcacb23ca46'),
('Sociology', 'The Department of Sociology equips students to engage in positive social change and develop a sociological imagination. It emphasizes diverse research methods and theoretical approaches to prepare students for professional opportunities and graduate study.', '["Sociology"]', '["Sociology"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/sociology.html#52e73cfac556'),
('English', 'The Department of English affords students a rich undergraduate education in the liberal arts centered on literature, cultural studies, and writing. Critical, professional, or creative writing projects are integral to every course in the English major. Students and faculty in the English Department discuss and write about British, American, and global literature, technical and professional communication, new media, and film. A range of theoretical approaches are used, sometimes with a focus on visual rhetoric and cultural studies. The department also offers the Creative Writing Minor, which provides students with a coherent course of study in the writing of poetry, fiction, and creative nonfiction, and the Professional Writing Minor, which focuses on the theories, ethics, and practice of writing in industry and public contexts. The English major and minors prepare students to read and write critically, to bring intellectual flexibility to academic and professional problems, and to enter the workforce as individuals with trained skills in analysis and self-expression.', '["English"]', '["English","Creative Writing","Professional Writing"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/english.html#efe14a4896d1'),
('Management and Entrepreneurship', 'The Department of Management and Entrepreneurship curriculum emphasizes rigorous analysis and managerial application. Courses cover organizational behavior, human resource management, team dynamics, leadership, sustainability, negotiations, strategic management, business ethics, entrepreneurship, new venture creation, and international management. As a management major, students learn to analyze complex business problems, apply appropriate strategies, and develop ethical reasoning, interpersonal competencies, leadership, communication, teamwork, and group dynamics skills applicable in both for-profit and for-purpose organizations.', '["Management"]', '[""]', '[""]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/management.html#0ee82f124271'),
('Political Science', 'The Department of Political Science offers a B.S. degree in political science, introducing students to the analysis of political behavior, values, institutions, and governments. It prepares students for graduate and professional studies and careers in public service, research, nonprofits, and the private sector. The department promotes experiential learning through internships in government agencies, legislative bodies, and nongovernmental organizations, including programs in Washington, D.C., and at the Panetta Institute, and encourages study abroad. Power & Identity courses examine power dynamics, inequalities, and their political consequences, addressing diversity, equity, and inclusion across all subfields of political science.', '["Political Science"]', '["Political Science"]', '["Public Sector Studies","International Relations","Pre-Law"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/political-science.html#f2580432317e'),
('Electrical and Computer Engineering', 'The Electrical and Computer Engineering Department offers B.S. degrees in Electrical Engineering and Electrical and Computer Engineering and supports a broad range of topics in design, construction, and operation of electrical systems and components as well as software and hardware integration for modern computing systems, with extensive laboratory facilities.', '["Electrical Engineering","Electrical and Computer Engineering"]', '["Electrical Engineering","Electrical and Computer Engineering"]', '[""]', 'School of Engineering', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/electrical-and-computer-engineering.html#95e2098e168c'),
('Bioengineering', 'Bioengineering focuses on applying biological, chemical, electrical, mechanical, and other engineering principles to understand, modify, or control biological systems. Students solve problems at the interface of engineering and the life sciences, preparing for careers in the medical device, biotechnology, and biopharmaceutical industries, graduate study, or entry into medical school.', '["Bioengineering"]', '["Bioengineering","Healthcare Innovation and Design"]', '["Biomolecular","Medical-device","Pre-med"]', 'School of Engineering', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/bioengineering.html#9cccaee2d064'),
('Ethnic Studies', 'Ethnic Studies is a dynamic, interdisciplinary field devoted to the critical examination of race, ethnicity, gender, sexuality, class, and intersecting forms of inequality as they affect historically marginalized populations. The department emphasizes comparative and transnational perspectives on African American/Black, Indigenous, Asian Pacific Islander and South Asian American, Chicana/o/x and Latina/o/x, and Middle Eastern and North African communities. Faculty engage students in research and teaching across themes such as community engagement, social movements, law and policy, intersectionality, education, immigration/globalization, and cultural analysis. The department offers a Bachelor of Science major in Ethnic Studies and four minors—Ethnic Studies, African American Studies, Asian American Studies, and Latina/o/x Studies—to prepare students for careers in social justice, government, education, law, medicine, community service, and for graduate study.', '["Ethnic Studies"]', '["Ethnic Studies","African American Studies","Asian American Studies","Latina/o/x Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/ethnic-studies.html#db9bedbcbb32'),
('Economics', 'Economics studies how individuals and societies allocate scarce resources to produce and distribute goods and services. The discipline addresses issues such as wealth and poverty, unemployment, inflation, international trade, and economic growth. The program’s rigorous analytical and quantitative training prepares graduates for careers in business, finance, government, education, and consulting, as well as for graduate study in economics, business, public policy, and law.', '["Economics"]', '["Economics"]', '["Data Analysis for Economics","Mathematical Economics"]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/economics.html#0b4e62ee6c48'),
('Music', 'The Department of Music offers a Bachelor of Arts in Music and a minor in Music, with coursework in theory, musicianship, listening, ethnomusicology, electronic music, applied instruction, and ensemble performance. A minor in musical theatre is also available in conjunction with the Department of Theatre and Dance.', '["Music"]', '["Music"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/music.html#98b1758c5b3d'),
('Modern Languages and Literatures', 'The Department of Modern Languages and Literatures educates students to be global citizens prepared to confront the complex challenges of an interdependent world. The faculty integrates language and culture teaching to enable students to communicate effectively across cultures, appreciate cultural diversity, interpret texts in sociocultural context, and analyze problems across boundaries. Rooted in Jesuit values and liberal arts, the Department offers rigorous programs and unique learning experiences both in and out of the classroom. Courses are offered in Arabic, Chinese, French, German, Italian, Japanese, and Spanish. BA degrees are offered in French and Francophone studies, Italian studies, and Spanish studies (with honors available for French majors). Minors are available in French and Francophone studies, Italian studies, Japanese studies, and Spanish studies. A placement recommendation is required for all language courses, and study abroad is strongly recommended. Many courses fulfill Core requirements, including Core Second Language, Cultures & Ideas, Diversity, Civic Engagement, ELSJ, and several Pathways.', '["French and Francophone Studies","Italian Studies","Spanish Studies"]', '["French and Francophone Studies","Italian Studies","Japanese Studies","Spanish Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/modern-languages-and-literatures.html#0fe0bb2f41c3'),
('Marketing', 'The Department of Marketing develops students’ decision-making and managerial skills with special emphasis on innovation, high technology, retailing, and digital marketing. It links a business to its markets and customers, guiding firms in customer analysis, communications, advertising, sales, and social media, and provides a foundation for general management or focused careers in advertising, retailing, sales, brand management, and market research.', '["Marketing"]', '["Marketing"]', '["Business and Technology Marketing","Consumer and Channel Marketing","Individually Designed Marketing"]', 'Leavey School of Business', 'https://www.scu.edu/bulletin/undergraduate/chapter-4-leavey-school-of-business/marketing.html#5e445d7bc235'),
('Theatre and Dance', 'The Department of Theatre and Dance celebrates creativity of the human spirit, offering a BA in Theatre Arts with emphases in Theatre or Dance and minors in Theatre, Dance, Theatre Design and Technology, and Musical Theatre. Emphasizing academic rigor, artistic discipline, and creative expression, students work closely with mentors and participate in faculty-directed plays, dance concerts, student productions, and guest-artist collaborations, preparing for diverse careers.', '["Theatre Arts"]', '["Theatre","Dance","Theatre Design and Technology"]', '["Theatre","Dance"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/theatre-and-dance.html#8d7a022b0b40'),
('Computer Science and Engineering', 'The Department of Computer Science and Engineering offers BS programs in Computer Science and Engineering and Web Design and Engineering, featuring a balanced software/hardware core, mathematical foundations, and interdisciplinary electives supported by the Engineering Computing Center and the Information Technology Center.', '["Computer Science and Engineering","Web Design and Engineering"]', '["Computer Science and Engineering","Responsible Artificial Intelligence"]', '[""]', 'School of Engineering', 'https://www.scu.edu/bulletin/undergraduate/chapter-5-school-of-engineering/computer-science-and-engineering.html#59ffa8ec905c'),
('Philosophy', 'The Department of Philosophy offers a Bachelor of Arts in philosophy. Philosophy inquires into the relation of human beings to the world—what we are, how we know, what values are, and how we live. Philosophical inquiry promotes analytical thinking and precise expression and provides excellent preparation for careers in law, government, finance, media, writing, and computer programming. To qualify for honors in philosophy, majors must maintain a 3.5 grade-point average in philosophy courses and complete PHIL 197 with a grade of A– or better.', '["Philosophy"]', '["Philosophy"]', '["Pre-Law and Justice","Ethics and Values","Science and Analysis","History of Philosophy"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/philosophy.html#2e5728c8beb5'),
('Religious Studies', 'The Department of Religious Studies offers a Bachelor of Arts in Religious Studies and a minor in theological and religious studies. In keeping with the University’s Catholic and Jesuit liberal‐education traditions, it provides courses in Scripture, history, and Catholic theology alongside a wide breadth of offerings in various religious traditions and methodologies. Departmental courses are clustered in three areas: Theology, Ethics, and Spirituality (TESP); Scripture and Tradition (SCTR); and Religion and Society (RSOC).', '["Religious Studies"]', '["Religious Studies"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/religious-studies.html#846b4ab8c333'),
('History', 'The History Department’s major program provides students with an understanding of the human experience through analysis of evidence from both recent and more distant past. As majors, students learn essential skills, geographical and thematic knowledge, and prepare for careers in education, journalism, media, government, law, business, and international affairs.', '["History"]', '["History"]', '[""]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/history.html#26de42993d7a'),
('Communication', 'The Department of Communication offers a bachelor of arts in communication, preparing students for careers in communication, entertainment, technology, journalism, and nonprofit sectors, as well as graduate studies in communication, film, journalism, and law. Students explore theories, research methods, responsibilities, institutional structures, and effects across mass, interpersonal, strategic, global, and computer-mediated communication, integrating theory and practice to create speeches, films, television programs, journalism, web content, and marketing campaigns. The program encourages a broad liberal arts education, integration with other departments, global studies via approved study abroad courses, and internships as elective credit. Students may pursue a general degree or one of six official emphases. A minor in digital filmmaking or journalism is also available. All courses must be 4–5 units, taken for a letter grade; practicum courses numbered 190–195 do not count toward majors or minors.', '["Communication"]', '["Journalism","Digital Filmmaking"]', '["Global Media","Culture","and Technologies","Leadership Communication","Communication","Diversity","and Culture","Film & Culture","Journalism","Strategic Communication"]', 'College of Arts And Sciences', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/communication.html#fc3a72b696d8');
INSERT INTO public.majors (name, description, department_code, requires_emphasis, course_requirements_expression, unit_requirements, other_requirements, other_notes, src) VALUES
('Biology', 'The biology major provides a solid foundation in modern biological concepts and practical skills, emphasizing laboratory and field work, and prepares students for graduate, medical, professional, teaching, research, and business careers.', 'BIOL', 0, '(BIOL1A & BIOL1B & BIOL1C) & (CHEM11 & CHEM12 & CHEM31 & CHEM32 & CHEM33) & ((PHYS11 & PHYS12 & PHYS13) | (PHYS31 & PHYS32 & PHYS33)) & ((MATH35 & MATH36) | (MATH11 & MATH12)) & BIOL101 & 7(BIOL100-199)', '[]', '[{"requirementName":"Laboratory Component","requirementDescription":"At least five of the seven upper-division biology courses must include a laboratory component."}]', 'Students are advised to take at least one upper-division course in each of the five biological concept categories (Evolution, Information Flow, Structure-Function, Systems, and Transformations of Energy and Matter).
INSERT INTO public.emphasis_areas_enhanced (name, description, applies_to, name_of_which_it_applies_to, department_code, course_requirements_expression, unit_requirements, other_requirements, other_notes, src) VALUES
('Applied Anthropology', 'Prepares students to apply anthropological knowledge and methods to human issues in fields such as health, development, environment, government, business, education, immigration, and poverty through coursework and internships.', 'Major', 'Anthropology', 'ANTH', '', '[]', '[]', '', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/anthropology.html#f7bf97d9b9e0'),
('Archaeology', 'Focuses on the study of the human past and archaeological methods, suitable for careers in cultural resource management or graduate study in archaeology.', 'Major', 'Anthropology', 'ANTH', '', '[]', '[]', '', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/anthropology.html#f7bf97d9b9e0'),
('Biological Anthropology', 'Offers in-depth training in biological anthropology, emphasizing interdisciplinary study of human evolution and modern human biological and cultural variation.', 'Major', 'Anthropology', 'ANTH', '', '[]', '[]', '', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/anthropology.html#f7bf97d9b9e0'),
('Arts Management', 'Optional for Art History majors: combines art history coursework with English, management, and marketing courses plus an internship.', 'Major', 'Art History', 'ARTH', '3((ARTH1-199) & !(ARTH198)) & ARTH198 & 1(ENGL100GR | ENGL101 | ENGL112) & 2(MGMT160 | MGMT170 | MGMT174) & 1(MKTG181 | MKTG186 | MKTG190)', '[]', '[{"requirementName":"Arts Management course designation","requirementDescription":"ARTH courses must be those designated Arts Management."}]', 'Completion of the emphasis will be noted on the student''s transcript.', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/art-and-art-history.html#f7994386b4fd'),
('Graphic Design', 'Optional for Studio Art majors: focuses on graphic design electives.', 'Major', 'Studio Art', 'ARTS', '1(ARTS70 | ARTS75) & 2(ARTS170 | ARTS173 | ARTS175 | ARTS177 | ARTS178 | ARTS180)', '[]', '[]', 'Completion of the emphasis will be noted on the student''s transcript.', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/art-and-art-history.html#f7994386b4fd'),
('Physics Track', 'Traditional physics track emphasizing advanced physics coursework, a technical elective, upper-division electives, and laboratory experience.', 'Major', 'Physics', 'PHYS', '(PHYS112 & PHYS120 & PHYS121 & PHYS122 & PHYS151) & 1(CSCI10 | COEN10 | COEN11 | MECH45 | CHEM12) & 3(PHYS104 | PHYS113 | PHYS116 | PHYS123 | PHYS161 | PHYS162 | PHYS171) & 1(PHYS113L | PHYS123L)', '[]', '[{"requirementName":"Chemistry exclusivity","requirementDescription":"CHEM12 and CHEM14 cannot both be taken for credit."}]', '', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/physics.html#6598cf74a8fa'),
('Biophysics Track', 'Biophysics track integrating biology, chemistry, and physics courses with biophysics-focused electives.', 'Major', 'Physics', 'PHYS', '(BIOL1A & BIOL1B & BIOL1C) & (CHEM12 | CHEM14) & (CHEM31 & CHEM32) & (PHYS120 | CHEM152) & (PHYS121 | CHEM151) & PHYS171', '[]', '[{"requirementName":"Elective units","requirementDescription":"Ten upper-division elective units chosen from specified biology, chemistry, bioengineering, and physics courses."},{"requirementName":"Core elective condition","requirementDescription":"At least one elective must be from PHYS if CHEM152 is taken in place of PHYS120 or CHEM151 is taken in place of PHYS121."}]', '', 'https://www.scu.edu/bulletin/undergraduate/chapter-3-college-of-arts-and-sciences/physics.html#6598cf74a8fa'),
('Data Analysis for Economics', 'Provides a structured course of study for economics majors to develop econometrics and data analysis skills and apply them to economic issues.', 'Major', 'Economics', 'ECON', 'ECON43 & 1(ECON173 | ECON174) & 3(ECON134 | ECON135 | ECON142 | ECON150 | ECON151 | ECON154 | ECON166 | ECON173 | ECON174 | ECON186 | ECON187 | ECON188)', '[]', '[{"requirementName":"Grade minimum","requirementDescription":"Courses in the concentration must be passed with a grade of C or higher."},{"requirementName":"Substitution approval","requirementDescription":"Substitutions require approval by the concentration advisor."}]', 'All upper-division courses in the concentration can count as upper-division electives toward the economics major.
INSERT INTO public.core_curriculum_requirements (name, description, applies_to, fulfilled_by, src) VALUES
('Critical Thinking and Writing 1', 'Two-course themed sequence. First course goals: Critical Thinking, Complexity, Communication; Meta-Goals: Information Literacy and Intentional Learning; focuses on reading and analyzing texts, composing rhetorically effective nonfiction, engaging multiple perspectives, and using writing as discovery.', 'All', '["N/A"]', 'https://www.scu.edu/provost/core/foundations/'),
('Critical Thinking and Writing 2', 'Second course goals: Critical Thinking, Complexity, Communication; Meta-Goals: Information Literacy and Intentional Learning; focuses on locating and selecting information from multiple credible perspectives, ethical use of sources, integrating sources effectively, and using writing and information literacy as tools for learning.', 'All', '["N/A"]', 'https://www.scu.edu/provost/core/foundations/'),
('Cultures and Ideas 1', 'Two-course themed sequence. First course goals: Global Cultures, Arts & Humanities, Critical Thinking, Perspective; focuses on identifying and analyzing significant cultural elements and contexts using disciplinary methods, and examining shared and diverse human experiences.', 'All', '["N/A"]', 'https://www.scu.edu/provost/core/foundations/'),
('Cultures and Ideas 2', 'Second course goals: Global Cultures, Arts & Humanities, Critical Thinking, Perspective; focuses on making connections between cultures and materials from C&I 1, understanding complexity, evaluating cultural interpretation effects, and analyzing human experiences relevant to students’ world views.', 'All', '["N/A"]', 'https://www.scu.edu/provost/core/foundations/'),
('Mathematics (Arts & Sciences except Life Sciences)', 'Foundations Mathematics requirement for College of Arts and Sciences (except Life Science majors). Goals: Critical Thinking, Mathematics & Quantitative Reasoning, Communication, Complexity. Students select any course from the approved list.', 'College of Arts and Sciences', '["MATH4","MATH6","MATH8","MATH11","MATH12","MATH30","MATH35","MATH36"]', 'https://www.scu.edu/provost/core/foundations/'),
('Mathematics (Leavey School of Business, enrolled before June 1, 2024)', 'Foundations Mathematics requirement for Business students enrolled (or previously enrolled) prior to June 1, 2024. Must take either MATH 30 and MATH 31, or MATH 11 and MATH 12.', 'Leavey School of Business', '["MATH11","MATH12","MATH30","MATH31"]', 'https://www.scu.edu/provost/core/foundations/'),
('Mathematics (Leavey School of Business, starting on or after June 1, 2024)', 'Foundations Mathematics requirement for Business students starting on or after June 1, 2024: one course (MATH 11 or MATH 30). Economics and Finance majors must take a second calculus course (MATH 12 or MATH 31).', 'Leavey School of Business', '["MATH11","MATH30","MATH12","MATH31"]', 'https://www.scu.edu/provost/core/foundations/'),
('Mathematics (Engineering)', 'Foundations Mathematics requirement for School of Engineering students. Must take Calculus & Analytic Geometry I and II.', 'School of Engineering', '["MATH11","MATH12"]', 'https://www.scu.edu/provost/core/foundations/'),
('Mathematics (Life Sciences)', 'Foundations Mathematics requirement for Life Science majors. Must take Calculus for Life Sciences I and II.', 'College of Arts and Sciences', '["MATH35","MATH36"]', 'https://www.scu.edu/provost/core/foundations/'),
('Religion, Theology, & Culture 1', 'Foundations requirement introducing central religious ideas and practices from distinct cultures; goals: Global Cultures, Critical Thinking, Complexity, Religious Reflection; students choose one course from approved offerings.', 'All', '["CLAS5","ENGR16","RSOC7","RSOC8","RSOC9","RSOC10","RSOC11","RSOC12","RSOC14","RSOC15","RSOC16","RSOC19","SCTR11","SCTR15","SCTR19","TESP2","TESP4","TESP16"]', 'https://www.scu.edu/provost/core/foundations/'),
